import React, { Component } from "react";
import Recipe from "./Recipe";

class InputForm extends Component {
  searchedQuery;
  constructor() {
    super();

    this.state = {
      query: "",
      recipeResult: []
    };
    this.onChange = this.onChange.bind(this);
  }
  onChange = event => {
    this.setState({ [event.target.name]: event.target.value });
    //const { query } = this.state;
    //localStorage.setItem("HistoryQuery", query);
  };
  getSearch = event => {
    event.preventDefault();
    const { query } = this.state;
    // this.getHistory();
    fetch(
      `https://api.edamam.com/search?q=${query}&app_id=${process.env.App_ID} &app_key=${process.env.APP_KEY}`
    )
      .then(response => response.json())
      .then(data =>
        this.setState({
          recipeResult: data.hits,
          query: ""
        })
      );
  };
  /* componentWillMount() {
    const { query } = this.state;
    localStorage.getItem("HistoryQuery") &&
      this.setState({
        query: localStorage.getItem("HistoryQuery")
      });
    console.log(query);
  }
  getHistory() {
     const data = localStorage.getItem("HistoryQuery");
    const { query } = this.state;

    this.setState({
      query: data
    });
    console.log(query);
  }*/

  componentWillUpdate(nextProps, nextState) {
    localStorage.setItem("HistoryQuery", JSON.stringify(nextState));
    localStorage.setItem("HistoryDate", Date.now());
  }
  componentDidMount() {
    this.searchedQuery = JSON.parse(localStorage.getItem("HistoryQuery"));
    if (localStorage.getItem("HistoryQuery")) {
      this.setState({
        query: this.searchedQuery.query
      });
    } else {
      this.setState({
        query: ""
      });
    }
  }
  render() {
    return (
      <div>
        <form onSubmit={this.getSearch} autoComplete="on">
          <input
            type="text"
            name="query"
            value={this.state.query}
            placeholder="Search ..."
            onChange={this.onChange}
          ></input>
          <button type="submit">Search</button>
        </form>

        <hr></hr>
        {this.state.recipeResult.map(item => (
          <Recipe
            key={item.recipe.uri}
            title={item.recipe.label}
            calories={item.recipe.calories}
            image={item.recipe.image}
            ingredients={item.recipe.ingredients}
          />
        ))}
      </div>
    );
  }
}
export default InputForm;
