import React, { Component } from "react";
import Recipe from "./component/Recipe";
class App extends Component {
  constructor() {
    super();
    this.state = {
      recipeResult: []
    };
  }
  searchRecipes = () => {
    //const req = `https://api.edamam.com/search?q=chicken&app_id=${process.env.App_ID} &app_key=${process.env.APP_KEY}`;

    fetch(
      `https://api.edamam.com/search?q=chicken&app_id=${process.env.App_ID} &app_key=${process.env.APP_KEY}`
    )
      .then(response => response.json())
      .then(data =>
        this.setState({
          recipeResult: data.hits.recipe
        })
      );
  };
  componentDidMount() {
    this.searchRecipes();
  }
  showSearchResult = () => {
    const { recipeResult } = this.state;
    {
      recipeResult.map(item => {
        return (
          <div className="list" key={item.word}>
            <li className="result-item">
              <h4 className="">{item.word}</h4>
            </li>
          </div>
        );
      });
    }
  };
  render() {
    return (
      <div>
        <form>
          <input type="text"></input>
          <button type="submit">Search</button>
        </form>
        {this.showSearchResult()}
      </div>
    );
  }
}
export default App;
