import React, { Component } from "react";

class InputForm extends Component {
  constructor() {
    super();
    this.state = {
      query: ""
    };
    this.onChange = this.onChange.bind(this);
  }
  onChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };
  getSearch = event => {
    event.preventDefault();
    // const url = `https://api.edamam.com/search?q=${query}&app_id=${process.env.App_ID} &app_key=${process.env.APP_KEY}`;
    const x = { query: this.state.query };
    console.log(x);
    fetch(
      `https://api.edamam.com/search?q=${x}&app_id=${process.env.App_ID} &app_key=${process.env.APP_KEY}`
    )
      .then(response => response.json())
      .then(data =>
        this.setState({
          recipeResult: data.hits
        })
      );
  };
  render() {
    return (
      <div>
        <form onSubmit={this.getSearch}>
          <input
            type="text"
            name="query"
            value={this.state.query}
            placeholder="Search ..."
            onChange={this.onChange}
          ></input>
          <button type="submit">Search</button>
        </form>
      </div>
    );
  }
}
export default InputForm;
