import React, { Component } from "react";
import Recipe from "./component/Recipe";
import InputForm from "./component/inputForm";
class App extends Component {
  constructor() {
    super();
    this.state = {
      recipeResult: []
    };
  }
  searchRecipes = () => {
    //const req = `https://api.edamam.com/search?q=chicken&app_id=${process.env.App_ID} &app_key=${process.env.APP_KEY}`;

    fetch(
      `https://api.edamam.com/search?q=chicken&app_id=${process.env.App_ID} &app_key=${process.env.APP_KEY}`
    )
      .then(response => response.json())
      .then(data =>
        this.setState({
          recipeResult: data.hits
        })
      );
  };
  componentDidMount() {
    this.searchRecipes();
  }
  delete(id) {
    this.setState(prevState => ({
      data: prevState.data.filter(el => el != id)
    }));
  }
  render() {
    return (
      <div>
        <InputForm />
        {this.state.recipeResult.map(item => (
          <Recipe
            key={item.recipe.uri}
            title={item.recipe.label}
            calories={item.recipe.calories}
            image={item.recipe.image}
            ingredients={item.recipe.ingredients}
            _handleDelete={this.delete.bind(this)}
          />
        ))}
      </div>
    );
  }
}
export default App;
