import React, { Component } from "react";
import Recipe from "./component/Recipe";
class App extends Component {
  constructor() {
    super();
    this.state = {
      recipeResult: {}
    };
  }
  searchRecipes = () => {
    //const req = `https://api.edamam.com/search?q=chicken&app_id=${process.env.App_ID} &app_key=${process.env.APP_KEY}`;

    fetch("https://jsonplaceholder.typicode.com/posts")
      .then(response => response.json())
      .then(data =>
        this.setState({
          recipeResult: data
        })
      );
  };
  componentDidMount() {
    this.searchRecipes();
  }
  render() {
    const postItem = this.state.recipeResult.map(item => {
      <div>
        <h3>{item.title}</h3>
        <p>{item.body}</p>
      </div>;
    });

    return (
      <div>
        <form>
          <input type="text"></input>
          <button type="submit">Search</button>
        </form>
      </div>
    );
  }
}
export default App;
