import React, { Component } from "react";
import Recipe from "./component/Recipe";
class App extends Component {
  constructor() {
    super();
    this.state = {
      recipeResult: {}
    };
  }
  searchRecipes = () => {
    //const req = `https://api.edamam.com/search?q=chicken&app_id=${process.env.App_ID} &app_key=${process.env.APP_KEY}`;

    fetch(
      `https://api.edamam.com/search?q=chicken&app_id=${process.env.App_ID} &app_key=${process.env.APP_KEY}`
    )
      .then(response => response.json())
      .then(data =>
        this.setState({
          recipeResult: data.hits
        })
      );
  };
  componentDidMount() {
    this.searchRecipes();
  }

  render() {
    const x = this.state.recipeResult.map(item => {
      <h4 className="">{item.recipe.label}</h4>;
    });
    return (
      <div>
        <form>
          <input type="text"></input>
          <button type="submit">Search</button>
        </form>
        {x}
      </div>
    );
  }
}
export default App;
