import React, { Component } from "react";
import Recipe from "./Recipe";
import moment from "moment";
import SearchHistory from "./SearchHistory";

import "InputForm.css";
class InputForm extends Component {
  constructor() {
    super();

    this.state = {
      query: "",
      recipeResult: []
    };
    this.onChange = this.onChange.bind(this);
  }
  onChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  };
  getSearch = event => {
    event.preventDefault();
    const { query } = this.state;
    const x = this.getQuery();
    x.push({ title: query, date: Date.now() });
    // banada
    // {history: banana, date: date}
    console.log(x);
    this.storeQuery(x);
    // get data from localStorage
    // push to data
    // insert to localStorage
    fetch(
      `https://api.edamam.com/search?q=${query}&app_id=${process.env.App_ID} &app_key=${process.env.APP_KEY}`
    )
      .then(response => response.json())
      .then(data =>
        this.setState({
          recipeResult: data.hits,
          query: "",
          title: this.getQuery()
        })
      );
  };
  /*
  getHistory() {
    const data = localStorage.getItem("HistoryQuery");
    const { query } = this.state;

    this.setState({
      query: data
    });
    console.log(query);
  }
*/

  storeQuery(historyArray) {
    localStorage.setItem("history", JSON.stringify(historyArray));
  }
  getQuery = () => {
    const history = localStorage.getItem("history");
    if (history) {
      const parsedData = JSON.parse(history);
      return parsedData;
    }
    return [];
  };

  initQuery() {
    this.storeQuery([]);
  }

  componentDidMount() {
    if (!this.getQuery()) {
      this.initQuery();
    }
  }
  /*
  componentDidMount() {
    this.searchedQuery = localStorage.getItem("HistoryQuery");
    if (localStorage.getItem("HistoryQuery")) {
      this.setState({
        title: this.state.title.query
      });
    } else {
      this.setState({
        title: ""
      });
    }
  }
  componentWillUpdate(nextProps, nextState) {
    //const { title } = this.state.title.query;
    if (this.state.title !== nextState.title) {
      const { title } = this.state;
      const dateTime = Date.now();
      localStorage.setItem("HistoryQuery", title.query);
      localStorage.setItem("HistoryDate", dateTime);
    }
  }

  .map(bar=><div></div>);
  .map(bar=>(<div> 
    asdasdas
    <div>)
    )
   .map(bar=>{
       return <div></div>
   })
*/ clearStorage = () => {
    localStorage.clear();
    this.setState({
      query: ""
    });
  };
  clearQuery = date => {
    const m = this.getQuery();
    const z = m.filter(item => item.date !== date);
    this.storeQuery(z);
    // Re-render
    this.setState({ ...this.state });
  };
  render() {
    return (
      <div className="container">
        <form onSubmit={this.getSearch} autoComplete="on">
          <input
            type="text"
            name="query"
            value={this.state.query}
            placeholder="Search ..."
            onChange={this.onChange}
          ></input>
          <button type="submit">Search</button>
        </form>
        <div>
          <div onClick={this.clearStorage}>Clear all</div>
          {this.getQuery().map(history => (
            <SearchHistory
              key={history.date}
              historyTitle={history.title}
              historyDate={moment(history.date).format(
                "MMMM Do YYYY, h:mm:ss a"
              )}
              clearBtn={
                <button onClick={() => this.clearQuery(history.date)}>
                  {" "}
                  X
                </button>
              }
            />
          ))}
        </div>
        {this.state.recipeResult.map(item => (
          <Recipe
            key={item.recipe.uri}
            title={item.recipe.label}
            calories={item.recipe.calories}
            image={item.recipe.image}
            ingredients={item.recipe.ingredients}
          />
        ))}
      </div>
    );
  }
}
export default InputForm;
